import MatiereService from "@/services/MatiereService";
const defaultState = () => ({
  paiements: [],
});
const usersModule = {
  state: defaultState(),
  mutations: {
    getMatieres(state, paiements) {
      console.log(paiements);
      state.paiements = paiements;
    },
  },
  actions: {
    getPaiements(context, query) {
      console.log(query);
      return MatiereService.get(query)
        .then((res) => {
          console.log(res);
          return context.commit("getPaiements", res.data);
        })
        .catch((err) => {
          return context.dispatch("add-error", err.response.data.message);
        });
    },
    //update paiement
    updateMatiere(context, paiement) {
      return MatiereService.update(paiement)
        .then(() => {
          return context.dispatch("getMatieres", {});
        })
        .catch((err) => {
          return context.dispatch("add-error", err.response.data.message);
        });
    },
    //delete paiement
    deleteMatiere(context, id) {
      return MatiereService.destroy(id)
        .then(() => {
          return context.dispatch("getMatieres", {});
        })
        .catch((err) => {
          return context.dispatch("add-error", err.response.data.message);
        });
    },
    addMatiere(context, paiement) {
      return MatiereService.create(paiement)
        .then(() => {
          return context.dispatch("getMatieres", {});
        })
        .catch((err) => {
          return context.dispatch("add-error", err.response.data.message);
        });
    },
    arrivageMatieres(context, paiements) {
      return MatiereService.arrivage(paiements)
        .then(() => {
          return context.dispatch("getMatieres", {});
        })
        .catch((err) => {
          return context.dispatch("add-error", err.response.data.message);
        });
    },
  },
  getters: {
    getPaiements: (state) => state.paiements,
  },
};

export default usersModule;
