import "@heroicons/vue/solid";
import { Listbox, ListboxButton, ListboxOptions, ListboxOption, Disclosure, DisclosureButton, DisclosurePanel } from "@headlessui/vue";
import { CheckIcon, SelectorIcon, ChevronUpIcon, MinusIcon, PlusIcon } from "@heroicons/vue/solid";
import { Icon } from "@iconify/vue";
import fileDownload from "js-file-download";
import Api from "@/services/api"; // import { Buffer } from "buffer";

export default {
  data() {
    return {
      errorFileModal: false,
      query: "",
      isEditing: false,
      newProduct: {
        produit: null,
        produits_commande: {
          prix: null,
          quantite: null
        }
      },
      observer: null,
      limit: 10,
      search: "",
      addedProducts: []
    };
  },

  methods: {
    handleBDC() {
      if (this.commande.bonDeCommande != null) {
        //todo
        //
        return Api.get(`/commande/${this.commande.id}/document`, {}, {
          responseType: "arraybuffer",
          reponseEncoding: "binary" // headers: { "Accept-Encoding": "gzip, deflate, br" },

        }).then(response => {
          fileDownload(response.data, `Bon De Commande ${this.commande.id}.pdf`, "application/pdf");
        }).catch(error => {
          console.log(error);
        });
      }

      var input = document.createElement("input");
      input.type = "file";
      input.click();
      input.onchange = this.uploadFile; // const formData = new FormData();
      //open file selector and get file
    },

    async uploadFile(event) {
      var file = event.target.files[0];

      if (file.type != "application/pdf") {
        console.log("File is not a pdf");
        this.errorFileModal = true;
        return;
      }

      var formData = new FormData();
      formData.append("file", file);
      this.$store.dispatch("uploadBonDeCommande", {
        formData,
        id: this.$route.params.id
      });
    },

    numbersOnly(event) {
      if (isNaN(parseInt(event.data))) event.target.value = event.target.value.slice(0, event.target.value.length - 1);
    },

    scrolllist(e) {
      if (e.target.scrollHeight - e.target.scrollTop == e.target.clientHeight && this.hasNextPage) this.limit += 10;
    },

    async onOpen() {
      if (this.hasNextPage) {
        await this.$nextTick();
        this.observer.observe(this.$refs.load);
      }
    },

    onClose() {
      this.observer.disconnect();
    },

    async infiniteScroll([{
      isIntersecting,
      target
    }]) {
      console.log(isIntersecting);

      if (isIntersecting) {
        const ul = target.offsetParent;
        const scrollTop = target.offsetParent.scrollTop;
        this.limit += 10;
        await this.$nextTick();
        ul.scrollTop = scrollTop;
      }
    },

    inputgroupfocushandler(e) {
      e.target.parentNode.classList.add("border");
      e.target.parentNode.classList.add("border-primary"); // e.target.parentNode.classList.add('border-blue-500')
    },

    inputgroupblurhandler(e) {
      e.target.parentNode.classList.remove("border");
      e.target.parentNode.classList.remove("border-primary"); // e.target.parentNode.classList.add('border-blue-500')
    },

    startEditMode() {
      this.isEditing = true;
    },

    deleteCommande() {
      this.$store.dispatch("deleteCommande", this.commande.id);
      this.$router.go(-1);
    },

    saveCommande() {
      if (this.$route.params.id == "new") {
        this.$store.dispatch("addCommande", this.commande);
      } else {
        this.$store.dispatch("updateCommande", this.commande);
        this.isEditing = false;
      }
    },

    addProduct() {
      if (this.newProduct.produit == null || this.newProduct.produits_commande.prix == null) return;
      if (!this.newProduct.produits_commande.quantite) this.newProduct.produits_commande.quantite = 1; //make api call /commandes/:id/addProduct

      this.$store.dispatch("commande/addProducts", {
        commandeId: this.commande.id,
        product: this.newProduct
      }); // this.addedProducts.push({
      // 	...this.newProduct.produit,
      // 	produits_commande: this.newProduct.produits_commande,
      // });

      this.newProduct = {
        produit: null,
        produits_commande: {
          prix: null,
          quantite: null
        }
      };
    },

    updateProduit(product) {
      console.log(product);
      this.$store.dispatch("commande/updateProduit", {
        commandeId: this.commande.id,
        product
      });
    },

    deleteProduit(id) {
      this.$store.dispatch("commande/deleteProduit", {
        commandeId: this.commande.id,
        productId: id
      });
      this.$store.dispatch("showCommande", this.commande.id);
    },

    validerCommande() {
      this.$store.dispatch("commande/validate", {
        idCommande: this.commande.id,
        next: this.$router
      });
    },

    save() {// if(this.$route.params.id == 'new'){}
      // this.$store.dispatch("commande/addProducts", {
      // 	commandeId: this.commande.id,
      // 	products: this.addedProducts,
      // });
      // this.addedProducts = [];
      // this.isEditing = false;
    }

  },
  components: {
    Listbox,
    ListboxButton,
    ListboxOptions,
    ListboxOption,
    CheckIcon,
    SelectorIcon,
    ChevronUpIcon,
    Disclosure,
    DisclosureButton,
    DisclosurePanel,
    MinusIcon,
    PlusIcon,
    Icon
  },

  beforeMount() {
    if (this.$route.params.id != "new") this.$store.dispatch("showCommande", this.$route.params.id);else {
      this.$store.dispatch("showCommande", "new");
      this.isEditing = true;
    }
    console.log(this.commande);
    this.$store.dispatch("getClients");
    this.$store.dispatch("getProduits");
  },

  mounted() {
    this.observer = new IntersectionObserver(this.infiniteScroll);
  },

  computed: {
    currentUser() {
      return this.$store.getters.getCurrentUser;
    },

    commande: {
      get() {
        return this.$store.getters.getCommande;
      },

      set(v) {
        this.$store.dispatch("setCommande", v);
      }

    },

    clients() {
      const r = this.$store.getters.getClients;
      return r.sort(function compare(a, b) {
        if (a.raisonSociale < b.raisonSociale) {
          return -1;
        }

        if (a.raisonSociale > b.raisonSociale) {
          return 1;
        }

        return 0;
      });
    },

    paginated() {
      return this.canAddProducts.slice(0, this.limit);
    },

    hasNextPage() {
      return this.paginated.length < this.canAddProducts.length;
    },

    products() {
      return this.$store.getters.getProduits;
    },

    canAddProducts() {
      let i = this.products.filter(el => {
        // console.log(thi)
        if (this.commande.Produits.find(d => d.nom == el.nom) || this.addedProducts.includes(d => d.nom == el.nom)) return false;
        if (this.search != "" && el.nom.toLowerCase().indexOf(this.search.toLowerCase()) == -1) return false;
        return true;
      });
      console.log({
        i
      });
      return i;
    }

  }
};
